    cursor: pointer;
    opacity: 0;
    width: 100%;
    height: 100%;
    box-sizing: border-box;
    padding: 0;
    margin: 0;
}
input.check:checked+.light{
    /* background: #19be6b; */
    animation: ani 3s infinite alternate;
}
.num{
    padding: 0 5px;
    border-radius:0 5px 5px 0;
    color: #fff;
    font-weight: bold;
    text-shadow: 0 -1px 1px #40a9ff;
